import React from 'react'
import cn from 'classnames'

import styles from './button.module.css'

function LinkButton({ full = false, children, className, ...props }) {
    return (
        <a
            type="button"
            className={cn(styles.button, full && styles.fullWidth, className)}
            {...props}
        >
            {children}
        </a>
    )
}

function BaseButton({ full = false, children, className, ...props }) {
    return (
        <button
            type="button"
            className={cn(styles.button, full && styles.fullWidth, className)}
            {...props}
        >
            {children}
        </button>
    )
}

function Button({ full = false, children, className, ...props }) {
    return (
        <button
            type="button"
            className={cn(styles.button, full && styles.fullWidth, className)}
            {...props}
        >
            {children}
        </button>
    )
}

export default Button
