import React from 'react'
import NavigationButton from './navigation-button'
import {
    Twitter,
    Home,
    HomeFill,
    Basket,
    Notification,
    NotificationFill,
    Profile,
    ProfileFill,
    BasketFill,
    SettingsFill,
    Settings,
} from './icon'
import TextTitle from './text-title'

const MENU = [
    {
        key: 'twitter',
        icon: <Twitter />,
        iconSelected: <Twitter />,
        title: '',
    },
    {
        key: 'home',
        icon: <Home />,
        iconSelected: <HomeFill />,
        title: 'Home',
    },
    {
        key: 'explore',
        icon: <Basket />,
        iconSelected: <BasketFill />,
        title: 'Explore',
    },
    {
        key: 'notification',
        icon: <Notification />,
        iconSelected: <NotificationFill />,
        title: 'Notification',
    },
    {
        key: 'messages',
        icon: <Profile />,
        iconSelected: <ProfileFill />,
        title: 'Messages',
    },
    {
        key: 'bookmark',
        icon: <SettingsFill />,
        iconSelected: <Settings />,
        title: 'Bookmark',
    },
]

import styles from './navigation.module.css'
function Navigation({ selectedKey = 'home' }) {
    return (
        <nav className={styles.nav}>
            {MENU.map((menu) => (
                <NavigationButton
                    key={menu.key}
                    selected={selectedKey === menu.key}
                >
                    {menu.icon}
                    {menu.title.length > 0 && (
                        <TextTitle>{menu.title}</TextTitle>
                    )}
                </NavigationButton>
            ))}
        </nav>
    )
}

export default Navigation
