import React, { useState } from 'react'
import styles from './table-price.module.css'
import TableAdress from './table-adress'
import { useRouter } from 'next/router'
import { CONTENT } from '../../constants'

function TablePrice({ href, children, ...props }) {
    const router = useRouter()
    const [nums, setNums] = useState('')
    const [price, setPrice] = useState('')
    const [sum, setSum] = useState('')
    const calculate = (e) => {
        setSum(e * price)
    }

    return (
        <div>
            {CONTENT.map((content) => {
                router.pathname === content.path
                return (
                    <table className={styles.tablePrice}>
                        <tr>
                            <th>Yemek</th>
                            <th>Fiyat</th>
                            <th>Adet</th>
                            <th>Tutar</th>
                        </tr>
                        <tr key={content.key}>
                            <td>
                                <b>{content.name}</b>{' '}
                                <small>{content.cont}</small>
                            </td>
                            <td>₺{content.deger}</td>
                            <td>
                                <input
                                    type="text"
                                    name={nums}
                                    onChange={(e) => calculate(e.target.value)}
                                />
                            </td>
                            <td>₺{sum}</td>
                        </tr>
                    </table>
                )
            })}
        </div>
    )
}
export default TablePrice
